import React, { useState }  from 'react'
import  {component} from 'react'
import { useEffect } from 'react';
import axios from 'axios'
import PropTypes from 'prop-types';
import Weather from './Weather';
import{
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";



export default function Page1() {
  
  const[form,setForm]=useState("");
  const handleonChange=(event)=>{
    setForm(event.target.value);
  }
  const[entered,setEntered]=useState('');
  
  const Submit=()=>{
    setEntered(form);
    alert(`The city is set to ${form}`);
  }
  const[weather,setWeather]=useState("None");
  let url=`https://api.openweathermap.org/data/2.5/weather?q=${form}&u\w.BMHOY0000000000000000000PJ
  
  its=metric&appid=cbb5ff06d459a15d93e615ba2cbe7613`;

  const getApi=()=>{
    fetch(url)
    .then((response)=>response.json())
    .then((data)=>{
      console.log(data);
      setWeather(data.main.temp);
    })
    .catch=(err)=>{
      console.log("Server Down");
    }
   

  }

 
  return (
    <div>
        <h1> Weather On GO </h1>
        <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="City" value={form} onChange={handleonChange}></input>
        <Router>
        <Link to="/card"><button type="button" className="btn btn-primary" onClick={getApi}>Get Weather</button></Link>
          <Switch>
            <Route exact path="/card"><Weather/></Route>
          </Switch>
        </Router>
        
      Weather: {weather}
    </div>
      )
}
