import React, { useState } from 'react'
import { useEffect } from 'react';

export default function Page1() {
  const[form,setForm]=useState("City");
  const handleonChange=(event)=>{
    setForm(event.target.value);
  }
  const[entered,setEntered]=useState('');
  const Submit=()=>{
    setEntered(form);
    alert(`The city is set to ${form}`);
    const[weather,setWeather]=useState('None');
  useEffect(() => {
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${entered}&appid=cbb5ff06d459a15d93e615ba2cbe7613`)
    .then(response => response.json())
    .then(data => setWeather(data.main.temp))
  },[])
  }

 
  return (
    <div>
        <h1> Weather On GO </h1>
        <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="City" value={form} onChange={handleonChange}></input>
        <button type="button" className="btn btn-primary" onClick={Submit}>Get Weather</button>
      Weather: {weather};
    </div>
  )
}
