import React, { useState }  from 'react'
import  {component} from 'react'
import { useEffect } from 'react';
import axios from 'axios'

export default function Page1() {
  const[form,setForm]=useState("City");
  const handleonChange=(event)=>{
    setForm(event.target.value);
  }
  const[entered,setEntered]=useState('');
  
  const Submit=()=>{
    setEntered(form);
    alert(`The city is set to ${form}`);
  
  }
  const[weather,setWeather]=useState("None");
  const url=`https://api.openweathermap.org/data/2.5/weather?q=${entered}&appid=cbb5ff06d459a15d93e615ba2cbe7613`;

  const getWeather=()=>{
    axios.get((url)).then((response)=>{
      const weatherdata=response.data
      setWeather(weatherdata);
    })
    .catch((error=>console.log('Error found')))
  }
useEffect( ()=>{
  getWeather();
},[] );
 
  return (
    <div>
        <h1> Weather On GO </h1>
        <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="City" value={form} onChange={handleonChange}></input>
        <button type="button" className="btn btn-primary" onClick={Submit}>Get Weather</button>
      Weather: {weather}
    </div>
      )
}
